import { useState,useEffect } from "react";
import { useQuery } from "react-query";

const useFetch = (repository, word) => {

  const [datas, setItems] = useState([]);
  const [isLoading, setLoading] = useState(false);

  const pageLimit = 10;
  const pageLimitAll = 5;

  //fetching filtered data for GitHub projects
  const fetchGitHubProjects = async (page = 1) => {
    const res = await fetch(
      `http://localhost:8000/items?q=math&_page=${page}&_limit=${pageLimit}`
    );
    return res.json();
  };

  function GitHubProjects() {
    const [page, setPage] = useState(1);
    const { data, status } = useQuery(["paginatedProjects", page], () => fetchGitHubProjects(page),{
      keepPreviousData: true
    });
    console.log(data);
    setItems(data);
  }

  //fetching filtered data for MATC projects
  const fetchMATCProjects = async (page = 1) => {
    const res = await fetch(
      `http://localhost:5000/items2?q=math&_page=${page}&_limit=${pageLimit}`
    );
    return res.json();
  };

  function MATCProjects() {
    const [page, setPage] = useState(1);
    const { data, status } = useQuery(["paginatedProjects", page], () => fetchMATCProjects(page),{
      keepPreviousData: true
    });
    console.log(data);
    setItems(data);
  }

  //fetching filtered data for MATC projects
  const fetchAllProjects = async (page = 1) => {
    const res1 = await fetch(
      `http://localhost:5000/items2?q=math&_page=${page}&_limit=${pageLimitAll}`
    );
    const res2 = await fetch(
      `http://localhost:8000/items?q=math&_page=${page}&_limit=${pageLimitAll}`
    );
    var res = [...res1, ...res2];
    return res.json();
  };

  function AllProjects() {
    const [pageA, setPageA] = useState(1);
    const { data, status } = useQuery(["paginatedProjects", pageA], () => fetchAllProjects(pageA),{
      keepPreviousData: true
    });
    console.log(data);
    setItems(data);
  }
  
  console.log(repository);
  console.log(word);
  setLoading(false);
  if (repository === "GitHub") 
  {
    setLoading(true);
    GitHubProjects();
    setLoading(false);
  } 
  else if (repository === "MATC") 
  {
    MATCProjects();
    setLoading(false);
  } else if (repository === "All") {
    try {
      //both json arrays are merged here
      AllProjects();
    } catch (e) {
      console.error(e);
    } finally {
      setLoading(false);
    }
  } else if (repository === "TYPE") {
    alert("Select repository type to begin search...");
  }
  return { datas, isLoading };
};

export default useFetch;
