import React, { useState,useEffect } from "react";
import ProjectList from "../views/ProjectList";
import { useQuery } from "react-query";
//Hosts the lists of projects matching search word
const Home = (props) => {
  const [data, setData] = useState([]);
  const [isLoading, setLoading] = useState(false);
  const word = props.string;
  const database = props.string2;
  const pageLimit = 10;

  const fetchProjects = async (page = 1) => {
    const res = await fetch(
      `http://localhost:8000/items?q=math&_page=${page}&_limit=${pageLimit}`
    );
    return res.json();
  };
  //data is the returned res.json() from fetchProjects()
  //status is the status of the function

  function Projects() {
    const [page, setPage] = useState(1);
    const { data, status } = useQuery(["paginatedProjects", page], () => fetchProjects(page),{
      keepPreviousData: false
    });
    setData(data);
  }
  //Projects();
  useEffect(()=>{
    Projects();
  },[word]);
  return (
    <div className="home">
      {isLoading && <div>Loading...</div>}

      <ProjectList items={data} repository={database} word={word} />
    </div>
  );
};

export default Home;
