import { useState,useEffect } from "react";
import { useQuery } from "react-query";

const useFetch = (repository, word) => {

  const [datas, setItems] = useState([]);
  const [isLoading, setLoading] = useState(false);


  useEffect(() => {
    console.log(repository);
    console.log(word);
    setLoading(false);
    if (repository === "GitHub") 
    {
      setLoading(true);
      const fetchGitHubProjects = async (page = 1) => {
        const res = await fetch(
          `http://localhost:8000/items?q=math&_page=${page}&_limit=${pageLimit}`
        );
        return setItems(res.json());
      };
      fetchGitHubProjects();
      setLoading(false);
    } 
    else if (repository === "MATC") 
    {
      setLoading(true);
      const fetchMATCProjects = async (page = 1) => {
        const res = await fetch(
          `http://localhost:5000/items2?q=math&_page=${page}&_limit=${pageLimit}`
        );
        return setItems(res.json());
      };
      fetchMATCProjects();
      setLoading(false);
    } else if (repository === "All") {
      try {
        //both json arrays are merged here
        const fetchAllProjects = async (page = 1) => {
          const res1 = await fetch(
            `http://localhost:5000/items2?q=math&_page=${page}&_limit=${pageLimitAll}`
          );
          const res2 = await fetch(
            `http://localhost:8000/items?q=math&_page=${page}&_limit=${pageLimitAll}`
          );
          var res = [...res1, ...res2];
          return setItems(res.json());
        };
        fetchAllProjects();
        setLoading(false);
      } catch (e) {
        console.error(e);
      } finally {
        setLoading(false);
      }
    } else if (repository === "TYPE") {
      alert("Select repository type to begin search...");
    }
  }, [word, repository]);

  return { datas, isLoading };
};

export default useFetch;
